<script>

var deselect_all = false;
$(document).ready(function() {
    $('#product-list').DataTable({
    	dom: 'Bfrtip',
        buttons: [
        	{
            	extend: 'colvis',
            	text: 'Customize Report',
            	className: 'customize_btn',
				collectionLayout: 'fixed four-column',				 
	            columnText: function ( dt, idx, title ) {
	                return `<label onClick='checkClick(${idx})' class='customInput'><input type='checkbox' id='checkClass${idx}'> `+title+'</label>';
	                // return title; checked='checked'
	            }
            },
        	{
                text: 'Generate Report',
                action: function ( e, dt, node, config ) {
                    $(`.export_buttons`).toggle();
                }
            },
            {
                extend: 'print',
                text: '<i class="fa fa-print text-secondary" aria-hidden="true"></i> Print',
                className: 'export_buttons',
                exportOptions: {
                    columns:':visible'
                },
                orientation : 'landscape',
                pageSize : 'A0',
            },{
                extend: 'excelHtml5',
                text: '<i class="fa fa-table text-success" aria-hidden="true"></i> Excel',
                className: 'export_buttons',
                exportOptions: {
                    columns: ':visible'
                }
            },
            {
                extend: 'pdfHtml5',
                text: '<i class="fa fa-book text-danger" aria-hidden="true"></i> Pdf',
                className: 'export_buttons',
                exportOptions: {
                    columns: ':visible'
                },
                orientation : 'landscape',
                pageSize : 'A0',
                customize: function ( doc ) {
                // Splice the image in after the header, but before the table
	                doc.content.splice( 1, 0, {
	                    margin: [ 0, 0, 0, 12 ],
	                    alignment: 'left',
	                    image: 'data:image/png;base64,',
	                });
	                // Data URL generated by http://dataurl.net/#dataurlmaker
	            }
            },
            {
                extend: 'colvisGroup',
                text: 'Hide all',
                hide: ':visible',
                className : 'hide_all_btn',
                columnDefs: [
            {
                targets: 1,
                className: 'noVis'
            }
        ],
            },
            {
                extend: 'colvisGroup',
                text: 'Show all',
                show: ':hidden',
                className : 'show_all_btn'
            }
            // 'colvis',
            // 'columnsToggle',
            // newly entered code
            
        ],columnDefs: [
            {
                targets: 1,
                className: 'noVis'
            }
        ],
        "ajax": {
            url : "<?php echo base_url(); ?>Report/get_emp_reports",
            type : 'GET',
        },
    });

	$('.show_all_btn').hide();
    $(`.export_buttons`).hide();
    $(`.hide_all_btn`).hide();

    var i=true;
    $('.customize_btn').click(() => {
		setTimeout(() => {
			if(deselect_all){
				$("input:checkbox").prop('checked', false);
				$('.hide_all_btn').click();
			}
		}, 100);	
		$('.show_all_btn').show();

    	if(i){    		
    		$('.hide_all_btn').click();
    		$("input:checkbox").prop('checked', false);
    		i= false;
    	}    	
    });
});

	function checkClick(id){		
		if($(`#checkClass${id}`).is(':checked')){
			$(`#checkClass${id}`).prop("checked", false);
		}else{
			$(`#checkClass${id}`).prop("checked", true);
		}

		$('.show_all_btn').click(() => {
			deselect_all = true;
			$('.show_all_btn').hide();
		});	
	}

</script>

